var msg = {};

msg.socketid = flow.get("clientid") || undefined;
if(global.get("user")!="admin"){
    msg.payload = { group: {
    show : ["Dashboard_SensorData"],
    hide : ["Dashboard_Signin","Dashboard_CreateUser","Operations_ADDDevice","Operations_UPDATEDEVICE","Operations_IDGENERATION"]
    }
};
}else{
msg.payload = { group: {
    show : ["Operations_IDGENERATION","Dashboard_SensorData","Operations_ADDDevice","Operations_UPDATEDEVICE","Operations_IDGENERATION"],
    hide : ["Dashboard_Signin"]
    }
};
}

flow.set("clientid", undefined);
return msg;
